using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEditor;
/// <summary>
/// custom editor for bool variable
/// </summary>
[CustomEditor(typeof(BoolVariable))]
public class BoolVariableEditor : Editor {
    public override void OnInspectorGUI()
    {
        base.OnInspectorGUI();
        BoolVariable boolVariable = (BoolVariable)target;
        if (Application.isPlaying)
            boolVariable.RuntimeValue = EditorGUILayout.Toggle("Runtime Value", boolVariable.RuntimeValue);
        else 
            GUILayout.Label("Runtime Value: " + boolVariable.RuntimeValue.ToString());
    }

    private static string icon = "";

    public override Texture2D RenderStaticPreview(string assetPath, Object[] subAssets, int width, int height)
    {
        return Base64ToTexture(icon);
    }

    private static Texture2D Base64ToTexture(string base64)
    {
        Texture2D t = new Texture2D(1, 1)
        {
            hideFlags = HideFlags.HideAndDontSave
        };
        t.LoadImage(System.Convert.FromBase64String(base64));
        return t;
    }

}
